package top.codecafe.kjframe.ui;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;public abstract class SupportFragment extends Fragment implements        OnClickListener {    public View fragmentRootView;    /**     * 一个私有回调类，线程中初始化数据完成后的回调     */    private interface ThreadDataCallBack {        void onSuccess();    }    protected abstract View inflaterView(LayoutInflater inflater,                                         ViewGroup container, Bundle bundle);    /**     * initialization widget, you should look like parentView.findviewbyid(id);     * call method     *     * @param parentView     */    protected void initWidget(View parentView) {    }    /**     * initialization data     */    protected void initData() {    }    public void onChangeVisible() {    }    /**     * widget click method     */    protected void widgetClick(View v) {    }    @Override    public void onClick(View v) {        widgetClick(v);    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        fragmentRootView = inflaterView(inflater, container, savedInstanceState);        initData();        initWidget(fragmentRootView);        return fragmentRootView;    }    protected void bindViewClick(int... array) {        if (array == null) return;        for (int id : array) {            fragmentRootView.findViewById(id).setOnClickListener(this);        }    }    protected <T extends View> T bindView(int id) {        return (T) fragmentRootView.findViewById(id);    }    protected <T extends View> T bindView(int id, boolean click) {        T view = (T) fragmentRootView.findViewById(id);        if (click) {            view.setOnClickListener(this);        }        return view;    }}